import{_ as e,c as r,o as t,ae as s}from"./chunks/framework.Dgg8-8ov.js";const l="/images/image%20(17).png",o="/images/image%20(64).png",i="/images/image%20(5).png",h="/images/image%20(47).png",n="/images/image%20(12).png",p="/images/image%20(4).png",c="/images/image%20(61).png",g="/images/image%20(41).png",m="/images/image%20(57).png",d="/images/image%20(6).png",S=JSON.parse('{"title":"Charles HTTPS抓包","description":"使用Charles抓包，配置都正确就是一直unknown","frontmatter":{"description":"使用Charles抓包，配置都正确就是一直unknown"},"headers":[],"relativePath":"notes/other/charles-https.md","filePath":"notes/other/charles-https.md","lastUpdated":null}'),P={name:"notes/other/charles-https.md"};function _(u,a,C,b,f,q){return t(),r("div",null,a[0]||(a[0]=[s('<h1 id="charles-https抓包" tabindex="-1">Charles HTTPS抓包 <a class="header-anchor" href="#charles-https抓包" aria-label="Permalink to &quot;Charles HTTPS抓包&quot;">​</a></h1><blockquote><ul><li>📅 <strong>日期</strong>：2019年10月02日 星期三</li><li>🌤 <strong>天气</strong>：多云微风</li></ul></blockquote><p><img src="'+l+'" alt=""></p><h2 id="准备" tabindex="-1">准备 <a class="header-anchor" href="#准备" aria-label="Permalink to &quot;准备&quot;">​</a></h2><ul><li><a href="https://www.charlesproxy.com/" target="_blank" rel="noreferrer">Charles</a>软件下载安装；</li><li>同一局域网下，两个设备 PC &amp; 需抓包设备；</li></ul><h2 id="pc配置" tabindex="-1">PC配置 <a class="header-anchor" href="#pc配置" aria-label="Permalink to &quot;PC配置&quot;">​</a></h2><p>打开软件，查看PC的IP和端口</p><h3 id="查看pc的ip" tabindex="-1">查看PC的IP <a class="header-anchor" href="#查看pc的ip" aria-label="Permalink to &quot;查看PC的IP&quot;">​</a></h3><p>**操作：**系统偏好设置 -&gt; 网络。<strong>记住PC的IP！！！</strong></p><p><img src="'+o+'" alt=" -gt; "></p><h3 id="查看charles端口" tabindex="-1">查看Charles端口 <a class="header-anchor" href="#查看charles端口" aria-label="Permalink to &quot;查看Charles端口&quot;">​</a></h3><p>**操作：**菜单栏 -&gt; Proxy -&gt; Proxy Settings。<strong>记住Charles的端口！！！</strong></p><p><img src="'+i+'" alt="HTTP Proxy Port8888"></p><h3 id="安装charles证书" tabindex="-1">安装Charles证书 <a class="header-anchor" href="#安装charles证书" aria-label="Permalink to &quot;安装Charles证书&quot;">​</a></h3><p>**操作：**菜单栏 -&gt; Help -&gt; SSL Proxying -&gt; Install Charles Root Certificate。</p><p><img src="'+h+'" alt="PCSSL"></p><p>显示Charles Proxy CA这个证书不被信任。</p><p><img src="'+n+'" alt=""></p><p>**操作：【**双击打开】列表中的Charles证书 -&gt; 【展开】信任 -&gt; 【修改】使用此证书时为【始终信任】，然后关闭此窗口即可。</p><p><strong>此时：</strong>【此根证书不被信任】变为【此证书已标记为受此账户信任】<strong>。</strong></p><p><img src="'+p+'" alt=""></p><h3 id="配置charles的ssl" tabindex="-1">配置Charles的SSL <a class="header-anchor" href="#配置charles的ssl" aria-label="Permalink to &quot;配置Charles的SSL&quot;">​</a></h3><p><strong>操作：</strong></p><ol><li>菜单栏 -&gt; Proxy -&gt; SSL Proxying Settings；</li><li>勾选 Enable SSL Proxying；</li><li>点击Add按钮，添加：*.443；</li><li>点击OK保存。</li></ol><p><img src="'+c+'" alt=""></p><h2 id="设备配置-iphone" tabindex="-1">设备配置（iPhone） <a class="header-anchor" href="#设备配置-iphone" aria-label="Permalink to &quot;设备配置（iPhone）&quot;">​</a></h2><h3 id="配置代理" tabindex="-1">配置代理 <a class="header-anchor" href="#配置代理" aria-label="Permalink to &quot;配置代理&quot;">​</a></h3><p><strong>操作：</strong></p><ol><li>手机打开：设置 -&gt; 无线局域网 -&gt; [所连接的无线网] -&gt; 配置代理；</li><li>选择【手动】；</li><li>填写服务为PC的IP；</li><li>填写端口为Charles的端口；</li><li>保存。</li></ol><h3 id="安装charles证书-1" tabindex="-1">安装Charles证书 <a class="header-anchor" href="#安装charles证书-1" aria-label="Permalink to &quot;安装Charles证书&quot;">​</a></h3><p>**操作：**手机使用Safari浏览器打开<a href="https://chls.pro/ssl" target="_blank" rel="noreferrer">chls.pro/ssl</a>，并允许安装。</p><p><img src="'+g+'" alt=""></p><h3 id="信任charles证书" tabindex="-1">信任Charles证书 <a class="header-anchor" href="#信任charles证书" aria-label="Permalink to &quot;信任Charles证书&quot;">​</a></h3><p>**操作：**手机打开：设置 -&gt; 通用 -&gt; 关于本机 -&gt; 证书信任设置，<strong>信任你安装的Charles证书</strong>。</p><p><img src="'+m+'" alt=""></p><h2 id="开始抓包" tabindex="-1">开始抓包 <a class="header-anchor" href="#开始抓包" aria-label="Permalink to &quot;开始抓包&quot;">​</a></h2><p><img src="'+d+'" alt=""></p><p>打开手机并请求，然后就能看到HTTPS的响应明文，<strong>微信小程序亦可。</strong></p>',38)]))}const k=e(P,[["render",_]]);export{S as __pageData,k as default};
